"use strict";(self.webpackChunkcodebank=self.webpackChunkcodebank||[]).push([[6053],{6053:(e,n,a)=>{a.r(n),a.d(n,{default:()=>i});const i=[{title:"ARIA labels and roles",examples:[{title:"Basic ARIA attributes",code:'\x3c!-- Button with aria-label --\x3e\n<button aria-label="Close dialog">\xd7</button>\n\n\x3c!-- Navigation with role --\x3e\n<nav role="navigation" aria-label="Main navigation">\n  <ul>\n    <li><a href="/">Home</a></li>\n    <li><a href="/about">About</a></li>\n  </ul>\n</nav>\n\n\x3c!-- Form with aria-describedby --\x3e\n<label for="password">Password:</label>\n<input type="password" id="password" aria-describedby="password-help">\n<div id="password-help">Password must be at least 8 characters long</div>\n\n\x3c!-- Progress bar --\x3e\n<div role="progressbar" aria-valuenow="75" aria-valuemin="0" aria-valuemax="100">\n  75% Complete\n</div>'},{title:"Landmarks and regions",code:'\x3c!-- Main content area --\x3e\n<main role="main">\n  <h1>Page Title</h1>\n  <p>Main content goes here...</p>\n</main>\n\n\x3c!-- Complementary content --\x3e\n<aside role="complementary" aria-label="Related articles">\n  <h2>Related Articles</h2>\n  <ul>\n    <li><a href="#">Article 1</a></li>\n    <li><a href="#">Article 2</a></li>\n  </ul>\n</aside>\n\n\x3c!-- Banner/header --\x3e\n<header role="banner">\n  <h1>Website Name</h1>\n</header>\n\n\x3c!-- Content info/footer --\x3e\n<footer role="contentinfo">\n  <p>&copy; 2024 Website Name</p>\n</footer>'}]},{title:"Semantic HTML for accessibility",examples:[{title:"Proper heading structure",code:"<h1>Main Page Title</h1>\n\n<section>\n  <h2>Section Title</h2>\n  <p>Section content...</p>\n  \n  <article>\n    <h3>Article Title</h3>\n    <p>Article content...</p>\n  </article>\n</section>\n\n<section>\n  <h2>Another Section</h2>\n  <p>More content...</p>\n</section>"},{title:"Accessible forms and inputs",code:'\x3c!-- Proper form labels --\x3e\n<form>\n  <label for="username">Username:</label>\n  <input type="text" id="username" name="username" required>\n  \n  \x3c!-- Fieldset for grouped inputs --\x3e\n  <fieldset>\n    <legend>Contact Information</legend>\n    \n    <label for="email">Email:</label>\n    <input type="email" id="email" name="email" required>\n    \n    <label for="phone">Phone:</label>\n    <input type="tel" id="phone" name="phone">\n  </fieldset>\n  \n  \x3c!-- Error message association --\x3e\n  <label for="age">Age:</label>\n  <input type="number" id="age" name="age" aria-describedby="age-error">\n  <div id="age-error" role="alert">Age must be between 0 and 120</div>\n</form>'}]}]}}]);
